import React, { useState } from 'react'
const Statistics = (props) => {
  if (props.stats.all === 0) {
    return (
      <div>
        <p>No Feedback given</p>
      </div>
    )
  } else {
    return (
      <div>
        <StatisticLine text={"good"} value={props.statistics.good.count}/>
      </div>
    )
  }
}

const StatisticLine = ({text, value}) => {
  return (
    <div>
      <p>{text} {value}</p>
    </div>
  )
}

