import React, { useState } from 'react'
const Statistics = (props) => {
  if (props.stats[3] === 0) {
    return (
      <div>
        <p>No Feedback given</p>
      </div>
    )
  } else {
    return (
      <div>
        <StatisticLine text={"good"} value={props.stats[0]}/>
        <StatisticLine text={"neutral"} value={props.stats[1]}/>
        <StatisticLine text={"bad"} value={props.stats[2]}/>
        <StatisticLine text={"all"} value={props.stats[3]}/>
        <StatisticLine text={"average"} value={props.stats[4]}/>
        <StatisticLine text={"positive"} value={props.stats[5]}/>
      </div>
    )
  }
}

const StatisticLine = (props) => {
  if (props.text !== "positive") {
    return (
      <div>
        <p>{props.text} {props.value}</p>
      </div>
    )
  }
  else {
    return (
      <div>
        <p>{props.text} {props.value * 100}%</p>
      </div>
    )
  }
}
const SetGood = () => setGood(props + 1)
const SetNeutral = () => setNeutral(props + 1)
const SetBad = () => setBad(props + 1)
const Button = (props) => {
  handleClick = props.handleClick
  return (
    <div>
      <button onClick={handleClick} text={props.text}>

      </button>
    </div>
  )
}
const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const all = good + neutral + bad
  const average = (good - bad) / (good + neutral + bad)
  const positive = good / (good + neutral + bad)
  const statistics = [good, neutral, bad , all , average, positive]
  return (
    <div>
      <h1>give feedback</h1>
      <Button onClick={() => setGood(good + 1)}good/>
      <Button text="neutral" />
      <Button onClick={() => setBad(bad + 1)}/>


      <h1>statistics</h1>
      <Statistics stats={statistics}/>
    </div>
  )
}

export default App
