import React, { useState, useEffect } from 'react'
import axios from 'axios'




const Filter = (props) => {
  return (
    <div> Find countries:
      <input value={props.newFilter}
      onChange={props.HandleFilterChange}
      />
    </div>
    )
}
const Country = (props) => {
  return (
    <div>
      <p>{props.name}</p>
    </div>
  )
}
const Countries = (props) => {
  //  .map(country =>
  //           <Country key={country.name.common} name={country.name.common}
  //           />
  //         )
  return(
    <div>
          {props.countriesToShow.reduce((result, item) => {
            if (result > 10) {
              <div>
                <p>Too many countries, specify another filter</p>
              </div>
            } else if (result < 1) {
                <div>
                <p>Apply a filter to search for countries</p>
              </div>
            }
            else {
              <Country key={item.name.common} name={item.name.common}/>
            }
              return result
              }, props.length)
          } 
      </div>
    )
}

const App = () => {
//   const [ persons, setPersons] = useState([]) 
  const [newFilter, setNewFilter] = useState('')
  const [newCountries, setNewCountries] = useState([])

  useEffect(() => {
  axios
    .get('https://restcountries.com/v3.1/all')
    .then(response => {
      setNewCountries(response.data)
    })
  }, [])

  const HandleFilterChange = (event) => {
    setNewFilter(event.target.value)
  }

  const countriesToShow = newFilter === ''
    ? newCountries
    : newCountries.filter(country => country.name.common
      .toLowerCase()
      .includes(newFilter.toLowerCase()))

  // var nameCount = 0
  //   duplicates = persons.reduce((result, item) => {
  //     result++;
  //     console.log(item.name);
  //     return result
  //   }, 0)
  //   if (duplicates !== 0) {
  //     window.alert(`${newName} is already added to phonebook`)
  //   } else {
  //     setPersons(persons.concat(nameObject))
  // }

  return (
    <div>
      <Filter newFilter={newFilter} HandleFilterChange={HandleFilterChange} />
      <Countries countriesToShow={countriesToShow} />
    </div>
  )
}
export default App
